package main

import (
	"encoding/base64"
	"encoding/hex"
	"fmt"
)

func main() {
	// Assuming you have the hexadecimal data in hexData variable.
	hexData := "" // Replace this with your actual hexadecimal data.

	// Convert hexadecimal to binary
	binaryData, err := hex.DecodeString(hexData)
	if err != nil {
		fmt.Println("Error decoding hexadecimal data:", err)
		return
	}

	// Convert binary to Base64
	base64Data := base64.StdEncoding.EncodeToString(binaryData)

	// Display or use the Base64 encoded data as needed
	fmt.Println(base64Data)

	//convert base64 to pdf

}
